# 反向映射关系（东方财富板块名称 -> 同花顺板块名称）
REVERSE_SECTORS_MAPPINGS = {}
for ths_sector, df_sectors in SECTORS_MAPPINGS.items():
    for df_sector in df_sectors:
        if df_sector not in REVERSE_SECTORS_MAPPINGS:
            REVERSE_SECTORS_MAPPINGS[df_sector] = []
        REVERSE_SECTORS_MAPPINGS[df_sector].append(ths_sector)

def get_industry_sectors():
    """
    获取所有行业板块名称
    
    Returns:
        list: 行业板块名称列表
    """
    return INDUSTRY_SECTORS.copy()

def get_industry_sectors_count():
    """
    获取行业板块总数
    
    Returns:
        int: 行业板块总数
    """
    return INDUSTRY_SECTORS_COUNT

def get_industry_categories():
    """
    获取行业板块分类
    
    Returns:
        dict: 行业板块分类字典
    """
    return INDUSTRY_CATEGORIES.copy()

def get_sectors_by_category(category):
    """
    根据分类获取行业板块
    
    Args:
        category (str): 分类名称
        
    Returns:
        list: 该分类下的行业板块列表
    """
    return INDUSTRY_CATEGORIES.get(category, [])

def search_sectors(keyword):
    """
    搜索包含关键词的行业板块
    
    Args:
        keyword (str): 搜索关键词
        
    Returns:
        list: 匹配的行业板块列表
    """
    return [sector for sector in INDUSTRY_SECTORS if keyword in sector]

def get_sector_index(sector_name):
    """
    获取行业板块在列表中的索引
    
    Args:
        sector_name (str): 行业板块名称
        
    Returns:
        int: 索引位置，如果不存在返回-1
    """
    try:
        return INDUSTRY_SECTORS.index(sector_name)
    except ValueError:
        return -1

def get_industry_category(industry_name: str) -> str:
    """
    根据行业名称获取对应的分类
    
    Args:
        industry_name (str): 行业板块名称
        
    Returns:
        str: 行业分类名称
    """
    for category, sectors in INDUSTRY_CATEGORIES.items():
        if industry_name in sectors:
            return category
    return "其他"  # 如果找不到分类，默认为"其他"

def get_stocks_by_sector(sector_name: str) -> list:
    """
    根据行业板块名称获取股票列表
    支持同花顺板块名称到东方财富板块名称的转换
    
    Args:
        sector_name (str): 行业板块名称（同花顺或东方财富）
        
    Returns:
        list: 该板块下的股票名称列表，如果板块不存在返回空列表
    """
    # 首先尝试直接匹配（东方财富板块名称）
    if sector_name in SECTORS_STOCKS:
        return SECTORS_STOCKS[sector_name]
    
    # 如果直接匹配失败，尝试通过映射关系转换（同花顺板块名称）
    if sector_name in SECTORS_MAPPINGS:
        mapped_sectors = SECTORS_MAPPINGS[sector_name]
        all_stocks = []
        
        # 合并所有映射板块的股票列表
        for mapped_sector in mapped_sectors:
            if mapped_sector in SECTORS_STOCKS:
                all_stocks.extend(SECTORS_STOCKS[mapped_sector])
        
        # 去重并保持顺序
        seen = set()
        unique_stocks = []
        for stock in all_stocks:
            if stock not in seen:
                seen.add(stock)
                unique_stocks.append(stock)
        
        return unique_stocks
    
    # 如果都找不到，返回空列表
    return []

def get_sector_by_stock(stock_name: str) -> list:
    """
    根据股票名称查询对应的行业板块
    返回同花顺板块名称列表（一只股票可能存在于多个板块）
    
    Args:
        stock_name (str): 股票名称
        
    Returns:
        list: 股票所属的同花顺板块名称列表，如果找不到返回空列表
    """
    result_sectors = []
    
    # 遍历所有东方财富板块，查找包含该股票的板块
    for df_sector, stocks in SECTORS_STOCKS.items():
        if stock_name in stocks:
            # 将东方财富板块名称转换为同花顺板块名称
            if df_sector in REVERSE_SECTORS_MAPPINGS:
                # 如果该东方财富板块映射到多个同花顺板块，添加所有映射
                ths_sectors = REVERSE_SECTORS_MAPPINGS[df_sector]
                result_sectors.extend(ths_sectors)
            else:
                # 如果没有映射关系，直接使用东方财富板块名称
                result_sectors.append(df_sector)
    
    # 去重并保持顺序
    seen = set()
    unique_sectors = []
    for sector in result_sectors:
        if sector not in seen:
            seen.add(sector)
            unique_sectors.append(sector)
    
    return unique_sectors

def get_stocks_by_category(category_name: str) -> dict:
    """
    根据行业分类获取所有板块的股票列表
    
    Args:
        category_name (str): 行业分类名称
        
    Returns:
        dict: 该分类下所有板块的股票列表字典
    """
    sectors = get_sectors_by_category(category_name)
    result = {}
    for sector in sectors:
        stocks = get_stocks_by_sector(sector)
        if stocks:  # 只返回有股票的板块
            result[sector] = stocks
    return result